var title = Trigger.EntryTitle
var subject = 'Ask Slashdot'

if (title.indexOf(subject) < 0) {
   IfNotifications.sendNotification.skip("Not an 'Ask Slashdot' article")
}

var Hour = Meta.currentUserTime.hour()
var Day = Meta.currentUserTime.day()

if (Day==6 || Day==7) {
  Slack.postToChannel.skip("Not a weekday")
}

else if (Hour < 7 || Hour > 9) {
  Slack.postToChannel.skip("Not morning commute hours")
}

var title = Trigger.EntryTitle
var subject = 'Mission'

if (title.indexOf(subject) < 0) {
   IosReadingList.createReadingListItem.skip("Not related to the Mission")
}

var EventType = Ecobee.motionDetected.EventType.toLowerCase()
if (EventType.indexOf("away") != -1) {
  Aquanta.cancelAway.skip()
} else if (EventType.indexOf("home") != -1) {
  Aquanta.away.skip()
}

var Hour = Meta.currentUserTime.hour()
var Day = Meta.currentUserTime.day()

// Skip outside of 7-10am and 5-7pm
if (Hour <7 || (Hour > 10 && Hour < 17) || Hour > 19) {
  Telegram.sendMessage.skip("Outside of commuting hours")
}

// Skip on weekends
if (Day == 6 || Day == 7) {
  Telegram.sendMessage.skip("Not a weekday")
}
